<?phpnamespace App\Helpers;use App\Models\CustomerToken;class ApiHelper{    function getCustomerID($request){    	// return "5d36b66bc870fc1ebc145272";        $customer = CustomerToken::where(array('token'=>$request->header('x-api-key')))->first();        return $customer->customer_id;    }    function getRefreshToken($request){        $headers = apache_request_headers();        return $headers['refresh_token'];        // return $request->header('refresh_token');    }}?>